import React from 'react';
import {ReactComponent as ShoppingIcon} from './../../assets/shop-bag.svg';
import {connect} from 'react-redux'
import {setCartItemCounter} from './../../redux/cart-reducer/cart.actions';
import './cart-icon.styles.scss';

const CartIcon = (cart) => {
    return (
        <div className="cart-icon">
            <ShoppingIcon className="shopping-icon" />
            {/* <span className="item-count">{cart.itemCounter}</span>
            <br /> */}
            <span onClick={() =>  setCartItemCounter()}>Add</span>
        </div>
    )
}

const mapStateToProps = ({cart}) => {
    return {
        itemCounter: cart.itemCounter
    }
}  

const mapDispatchToProps = dispatch => ({
    setCartItemCounter: () => dispatch(setCartItemCounter())
})

export default connect(mapStateToProps, mapDispatchToProps)(CartIcon);
