import React from 'react';

import './sign-in.styles.scss';

import FormInput from './../../components/form-input/form-input.component';

class SignIn extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        }
    }

    handleSubmit = event => {

        // #### IT WILL HIJACK THE ACTUAL CONTROL < WHAT GONNA SUBMIT AFTER IT.
        event.preventDefault();

        this.setState({
            email: '',
            password: ''
        })

    }

    handleChange = event => {
        const { value, name } = event.target;
        this.setState({ [name]: value })
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with you email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput name="email" type="email" label="Email" handleChange={this.handlehandleChange}  required />
                    <label>Email</label>

                    <FormInput name="password" type="password" label="Password" handleChange={this.handleChange} required />
                    <label>Password</label>

                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default SignIn;
