import {createSelector} from 'reselect';

const selectShop = state => state.shop;

export const selectShopData = createSelector(
    [selectShop], 
    (shop) => shop.collections
)

export const selectShopByCategory = createSelector(
    [selectShopData],
    (collections) => collections.filter(collection => collection.routeName == 'hats') 
)
