const INITIAL_CART = {
    itemCounter: 5
}
const cartReducer = (state = INITIAL_CART, action) => {

    switch(action.type) {

        case 'CART_ITEM_UP': 
            return {
                ...state,
                itemCounter: action.payload
            }
        
        default: return state;            
    }
}

export default cartReducer;
