import React from 'react';
import {connect} from 'react-redux';
import {setCartItemCounter} from './../../redux/cart-reducer/cart.actions';
import './collection-item.styles.scss';
import CustomButton from './../custom-button/custom-button.component';


const CollectionItem = ({name, imageUrl, price, setCartItemCounter}) => {
    return (
        <div className='collection-item'>
            <div className='image' style={{
                backgroundImage: `url(${imageUrl})`
            }}></div>
            <div className='collection-footer'>
                <span className='name'>{name}</span>
                <span className='price'>{price}</span>
            </div>
            <CustomButton inverted onClick={() => setCartItemCounter(1)}>Add to cart</CustomButton>
        </div>
    )
}

const mapDispatchToProps = dispatch => ({
    setCartItemCounter: (value) => dispatch(setCartItemCounter(value))
})
export default connect(null, mapDispatchToProps)(CollectionItem);
