import React from 'react';
import {connect} from 'react-redux';
import {selectShopByCategory} from './../../redux/shop-reducer/shop.selectors.js';
import CollectionItem from './../../components/collection-item/collection-item.component';
import './category.styles.scss';

const CategoryPage = ({match}) => {
    console.log(match);
    return (
        <div className="category">
            CATEGORY PAGE AFTER NESTED ROUTING
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    collection: selectShopByCategory(state, ownProps.match.params.categoryId)
})

export default connect(mapStateToProps, null)(CategoryPage);
