import React from 'react';
import {connect} from 'react-redux';
import {removeItem} from './../../redux/cart-reducer/cart.actions';

import './checkout-item.styles.scss';

const CheckoutItem = ({cartItem: {id, name, quantity, price, imageUrl}, dispatch}) => {
    return (
        <div className="checkout-item">
            <div className="image-container">
                <img src={imageUrl} alt="item" />
            </div>
            <span className="name">{name}</span>
            <span className="quantity">{quantity}</span>
            <span className="price">{price}</span>
            <div className="remove-button" onClick={() => dispatch(removeItem({id, name, quantity, price, imageUrl}))}>
                &#10005;
            </div>
        </div>
    )
}


export default connect()(CheckoutItem);
