const INITIAL_CART = {
    itemCounter: 5
}
const cartReducer = (state = INITIAL_CART, action) => {

    switch(action.type) {

        case 'CART_ITEM_UP': 
            console.log('EVENT CAUGHT')
            return {
                ...state,
                itemCounter: state.cart.itemCounter + action.payload
            }
        
        default: return state;            
    }
}

export default cartReducer;
